$pattern: (
  "up": translateY(100px),
  "down": translateY(-6px),
  "left": translateX(40px),
  "right": translateX(-100px),
);

@each $key, $value in $pattern {
  .appear.#{$key} {
    & .item {
      transform: $value;
    }
  }
}
.appear {
  & .item {
    transition: all 0.8s;
    opacity: 0;
  }
  &.inview {
    & .item {
      opacity: 1;
      transform: none;

      @for $i from 1 through 10 {
        &:nth-child(#{$i}) {
          transition-delay: $i * 0.1s;
        }
      }
    }
  }
}

@keyframes down {
  0%,
  100% {
    transform: translateY(-10px);
    opacity: .1;
  }
  60% {
    transform: translateY(0);
    opacity: 1;
  }
}

