    // width: 100%;
    max-width: 1050px;
    height: 50px;
    opacity: 0;
    transition: 1s;
    // transition-delay: 2s;

    &.inview {
      opacity: 1;

      .arrow {
        &.down {
          transform-origin: top;
          animation: down 2s linear infinite;
        }
        &.right {
          transform-origin: left;
          animation: right 2s linear infinite;
        }
      }
    }
  }
}

