        width: 100vw;
        height: 1px;
        display: block;
        background-color: $white;
        transition: all 1.5s ease 0.2s;
        transition-delay: 0.5s;
        // filter: drop-shadow(-3px -3px 2px rgba($white, 0.8))
        //   drop-shadow(5px 5px 5px rgba($white, 0.8));
        // filter: none;
      }

      &::before {
        right: 100%;
        transform: translateY(-50%) scaleX(0);
        transform-origin: right;
      }
      &.inview::before {
        transform: translateY(-50%);
      }
      &::after {
        left: 100%;
        transform: translateY(-50%) scaleX(0);
        transform-origin: left;
      }
      &.inview::after {
        transform: translateY(-50%);
      }
    }
  }

  .content {
    position: relative;
    z-index: 200;
    width: 100%;
    max-width: 1050px;
    margin: 0 auto;
    opacity: 0;
    transform: translateY(50px);
    transition: opacity 0.3s, transform 0.5s;

    &.active {
      opacity: 1;
      transform: none;
    }

    p {
      margin: 10px 0;
      font-size: $fsP;
      font-weight: 700;
    }
  }
}

.header {
  position: fixed;
  z-index: 1000;
  width: 100vw;
  height: $headerHeight;
  transition: 0.5s;

  &__inner {
    position: relative;
    // display: flex;
    // justify-content: space-between;
    // align-items: center;
    width: 100%;
    height: 100%;
    max-width: 1050px;
    margin: 0 auto;
    padding: 0 10px;

    h2 {
      position: absolute;
      left: 10px;
      padding-right: 15px;
      letter-spacing: 1px;
      font-size: $fsH2;
      color: $black;
      @extend .logo;
    }

    .buttons {
      position: absolute;
      top: 50%;
      right: 10px;
      transform: translateY(-50%);
      display: flex;
      align-items: center;
      opacity: 0;
      visibility: hidden;
      transition: 0.5s;
      transition-delay: 0.5s;

      a {
        width: $instagramIconSize;
        height: $instagramIconSize;
        display: inline-block;
        border-radius: 24px;
        overflow: hidden;
        transition: 0.3s linear;
        position: relative;

        &:hover {
          // transform: scale(1.1);
          border-radius: 50%;

          i::before {
            background-position: 0% 0%;
          }
        }

        i::before {
          position: absolute;
          width: 100%;
          height: 100%;
          text-align: center;
          background: linear-gradient(
            135deg,
            rgba(255, 150, 66, 0.9) 50%,
            rgba(21, 21, 21, 0.9) 50%
          );
          background-size: 200% 200%;
          background-position: 100% 100%;
          top: 0;
          left: 0;
          line-height: $instagramIconSize;
          -webkit-background-clip: text;
          -webkit-text-fill-color: transparent;
          transition: background 0.5s;
        }
      }
      i {
        font-size: $instagramIconSize;
      }

      .selected-language {
        position: relative;
        margin-left: 10px;

        img {
          display: block;
          width: 42px;
          height: 28px;
          cursor: pointer;
          transition: 0.5s;

          &:hover {
            transform: scale(1.1);
          }
        }
      }

      .languages {
        position: absolute;
        top: 40px;
        left: -60px;
        width: 170px;
        background-color: rgba($black, 0.6);
        list-style: none;
        opacity: 0;
        visibility: hidden;
        overflow: hidden;
        transition: 0.8s;
        // display: none;

        &.open {
          opacity: 1;
          visibility: visible;

          li {
            transform: none;
          }
        }
        @for $i from 1 through 5 {
          li:nth-child(#{$i}) {
            transition-delay: 0.1s * $i, 0s;
          }
        }

        li {
          display: flex;
          align-items: center;
          // margin: 10px 0;
          padding: 10px;
          border-bottom: 1px solid rgba($white, 0.1);
          cursor: pointer;
          transform: translateX(100%);
          transition-duration: 0.3s;
          transition-property: transform, background-color;

          &.selected {
            background-color: rgba($orange, 0.6);
            &:hover {
              background-color: rgba($orange, 0.7);
            }
          }
          &:hover {
            background-color: rgba($orange, 0.4);
          }

          img {
            width: 48px;
            height: 32px;
            margin-right: 10px;
          }

          span {
            font-size: 16px;
            font-weight: 700;
            color: $white;
          }
        }
      }
    }
  }
}

